// App.js
import React, { useEffect } from 'react';
import { Magic } from 'magic-sdk';
import { OpenIdExtension } from '@magic-ext/oidc';
import { useAuth0 } from "@auth0/auth0-react";

// Initialize the Magic instance with the provided keys
const magic = new Magic('pk_live_CC508AE07CC19E8F', {
  extensions: [new OpenIdExtension()],
});

function App() {
  const { loginWithRedirect, getIdTokenClaims, logout, isAuthenticated } = useAuth0();

  useEffect(() => {
    if (isAuthenticated) {
      getMagicWallet();
    }
  }, [isAuthenticated]);

  const getMagicWallet = async () => {
    const tokenClaims = await getIdTokenClaims();
    const didToken = await magic.openid.loginWithOIDC({
      jwt: tokenClaims.__raw,
      providerId: 'IisxMokNajXzQDjiKGKU2g02Z73wNRWKUq61lRL6zU8=',
    });
    // You can now use the DID token for further actions
  };

  return (
    <div className="App">
      <header className="App-header">
        {/* Reference the image from the public folder */}
        <img src="/Magic_Logo.jpeg" alt="Magic logo" />
        <p>Welcome to Magic Auth Example</p>
        {!isAuthenticated ? (
          <button onClick={loginWithRedirect}>Login</button>
        ) : (
          <button onClick={() => logout({ returnTo: window.location.origin })}>
            Logout
          </button>
        )}
      </header>
    </div>
  );
}

export default App;
